import React from "react";
import AddCar from "../components/AddCar";

export default function CarsForSale({ user, cars, onAddCar }) {
  return (
    <div className="cars-for-sale">
      <h2>מכוניות למכירה</h2>
      <div
        className="cars-list"
        style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}
      >
        {cars.map((car) => (
          <div
            key={car.id}
            className="car-item"
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "16px",
              width: "300px",
              textAlign: "center",
            }}
          >
            <img
              src={car.image}
              alt={car.name}
              onError={(e) => (e.target.src = "/images/default-car.jpg")}
              style={{ width: "100%", height: "200px", objectFit: "cover" }}
            />
            <h3>{car.name}</h3>
            <p>קילומטראז': {car.mileage} ק"מ</p>
            <p>בעלות: {car.owner}</p>
            <p>מחיר: {car.price}</p>
          </div>
        ))}
      </div>
      {user && (
        <div style={{ marginTop: "20px" }}>
          <h3>הוסף רכב למכירה</h3>
          <AddCar onAdd={onAddCar} />
        </div>
      )}
    </div>
  );
}
