import React, { useState } from "react";

const AddCar = ({ onAddCar }) => {
  const [form, setForm] = useState({
    name: "",
    price: "",
    mileage: "",
    owner: "",
    image: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setForm((prev) => ({ ...prev, image: e.target.files[0] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const isFormValid = Object.values(form).every((value) => value);
    if (!isFormValid) {
      alert("אנא מלא את כל השדות");
      return;
    }
    onAddCar(form);
    alert("הרכב נוסף בהצלחה!");
    setForm({ name: "", price: "", mileage: "", owner: "", image: null });
  };

  return (
    <form className="add-car-form" onSubmit={handleSubmit}>
      <h2>הוסף רכב למכירה</h2>
      <input
        type="text"
        name="name"
        value={form.name}
        onChange={handleChange}
        placeholder="שם הרכב"
      />
      <input
        type="number"
        name="price"
        value={form.price}
        onChange={handleChange}
        placeholder="מחיר"
      />
      <input
        type="number"
        name="mileage"
        value={form.mileage}
        onChange={handleChange}
        placeholder="קילומטראז'"
      />
      <input
        type="text"
        name="owner"
        value={form.owner}
        onChange={handleChange}
        placeholder="בעלות (יד)"
      />
      <input type="file" name="image" onChange={handleFileChange} />
      <button type="submit">הוסף</button>
    </form>
  );
};

export default AddCar;
