import React, { useState } from "react";
import AddCar from "../components/AddCar";

const initialCars = [
  {
    id: 1,
    name: "Lamborghini Huracan",
    price: "1,500,000 ₪",
    mileage: 5000,
    owner: "יד ראשונה",
    image: "/images/lamborghini-aventador.jpg",
  },
  {
    id: 2,
    name: "Ferrari F8 Tributo",
    price: "1,800,000 ₪",
    mileage: 3000,
    owner: "יד ראשונה",
    image: "/images/ferrari-f40.jpg",
  },
  {
    id: 3,
    name: "Porsche 911 Carrera",
    price: "950,000 ₪",
    mileage: 15000,
    owner: "יד שנייה",
    image: "/images/porsche-911.jpg",
  },
];

export default function CarsForSale({ user }) {
  const [cars, setCars] = useState(initialCars);

  const handleAddCar = (newCar) => {
    const carWithImage = {
      ...newCar,
      image: newCar.image || "/images/default-car.jpg",
      owner: user.email,
    };
    setCars([...cars, carWithImage]);
  };

  return (
    <div className="cars-for-sale">
      <h2>מכוניות למכירה</h2>
      <div className="cars-list">
        {cars.map((car) => (
          <div key={car.id} className="car-item">
            <img
              src={car.image}
              alt={car.name}
              onError={(e) => (e.target.src = "/images/default-car.jpg")}
            />
            <h3>{car.name}</h3>
            <p>קילומטראז': {car.mileage} ק"מ</p>
            <p>בעלות: {car.owner}</p>
            <p>מחיר: {car.price}</p>
          </div>
        ))}
      </div>
      {user && <AddCar onAdd={handleAddCar} />}
    </div>
  );
}
