import React, { useState } from "react";

import { Link } from "react-router-dom";

const cars = [
  {
    id: 1,
    name: "Lamborghini Huracan",
    price: "1,500,000 ₪",
    image: "/images/lamborghini-aventador.jpg",
    description: "מכונית ספורט עוצמתית עם ביצועים יוצאי דופן ועיצוב יוקרתי.",
  },
  {
    id: 2,
    name: "Ferrari F8 Tributo",
    price: "1,800,000 ₪",
    image: "/images/ferrari-f40.jpg",
    description: "פרארי קלאסית עם מנוע V8 עוצמתי וסטייל איטלקי אמיתי.",
  },
  {
    id: 3,
    name: "Porsche 911 Carrera",
    price: "950,000 ₪",
    image: "/images/porsche.jpg",
    description: "מכונית ספורט אלגנטית וחדשנית המתאימה לנהיגה יומיומית.",
  },
];

const CarsForSale = () => {
  return (
    <div className="cars-container">
      <h2>מכוניות למכירה</h2>
      <div className="car-list">
        {cars.map((car) => (
          <div key={car.id} className="car-card">
            <img src={car.image} alt={car.name} className="car-image" />
            <h3>{car.name}</h3>
            <p>{car.description}</p>
            <p>מחיר: {car.price}</p>
            <Link to="/purchase">
              <button className="buy-button">קנה עכשיו</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CarsForSale;
