import React, { useState } from "react";
import AddCar from "../components/AddCar";
import { Link } from "react-router-dom";

const cars = [
  {
    id: 1,
    name: "Lamborghini Huracan",
    price: "1,500,000 ₪",
    image: "/images/lamborghini-aventador.jpg",
    description: "מכונית ספורט עוצמתית עם ביצועים יוצאי דופן ועיצוב יוקרתי.",
  },
  {
    id: 2,
    name: "Ferrari F8 Tributo",
    price: "1,800,000 ₪",
    image: "/images/ferrari-f40.jpg",
    description: "פרארי קלאסית עם מנוע V8 עוצמתי וסטייל איטלקי אמיתי.",
  },
  {
    id: 3,
    name: "Porsche 911 Carrera",
    price: "950,000 ₪",
    image: "/images/porsche.jpg",
    description: "מכונית ספורט אלגנטית וחדשנית המתאימה לנהיגה יומיומית.",
  },
];

const CarsForSale = () => {
  const [cars, setCars] = useState([
    {
      id: 1,
      name: "טויוטה קורולה",
      price: 50000,
      mileage: 120000,
      owner: "יד שנייה",
      image: "/images/toyota.jpg",
    },
  ]);

  const handleAddCar = (newCar) => {
    setCars((prevCars) => [
      ...prevCars,
      { ...newCar, id: prevCars.length + 1 },
    ]);
  };

  return (
    <div className="cars-for-sale">
      <AddCar onAddCar={handleAddCar} />
      <div className="cars-container">
        {cars.map((car) => (
          <div key={car.id} className="car-item">
            <h3>{car.name}</h3>
            <p>מחיר: {car.price} ש"ח</p>
            <p>קילומטראז': {car.mileage} ק"מ</p>
            <p>בעלות: {car.owner}</p>
            {car.image && (
              <img src={URL.createObjectURL(car.image)} alt={car.name} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CarsForSale;
