import React, { useState } from "react";

const AddCar = ({ onAddCar }) => {
  const [form, setForm] = useState({
    name: "",
    price: "",
    mileage: "",
    owner: "",
    image: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setForm((prev) => ({ ...prev, image: e.target.files[0] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !form.name ||
      !form.price ||
      !form.mileage ||
      !form.owner ||
      !form.image
    ) {
      alert("אנא מלא את כל השדות");
      return;
    }
    onAddCar(form);
    alert("הרכב נוסף בהצלחה!");
    setForm({ name: "", price: "", mileage: "", owner: "", image: null });
  };

  return (
    <div className="add-car-container">
      <h2>הוסף רכב למכירה</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>שם הרכב:</label>
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            placeholder="לדוגמה: טויוטה קורולה"
          />
        </div>
        <div className="form-group">
          <label>מחיר:</label>
          <input
            type="number"
            name="price"
            value={form.price}
            onChange={handleChange}
            placeholder="לדוגמה: 50000"
          />
        </div>
        <div className="form-group">
          <label>קילומטראז':</label>
          <input
            type="number"
            name="mileage"
            value={form.mileage}
            onChange={handleChange}
            placeholder="לדוגמה: 120000"
          />
        </div>
        <div className="form-group">
          <label>בעלות (איזו יד):</label>
          <input
            type="text"
            name="owner"
            value={form.owner}
            onChange={handleChange}
            placeholder="לדוגמה: יד שנייה"
          />
        </div>
        <div className="form-group">
          <label>תמונה:</label>
          <input type="file" name="image" onChange={handleFileChange} />
        </div>
        <button type="submit">הוסף</button>
      </form>
    </div>
  );
};

export default AddCar;
